member.example:
This example shows how to create a PDF out of a Tiff image.

            
            [Visual&nbsp;Basic]
            Imports System
            Imports ceTe.DynamicPDF
            Imports ceTe.DynamicPDF.Imaging
            
            Module MyModule
            		
            	Sub Main()
            		' Create a TiffFile object from the TIFF image
            		Dim myTiffFile As TiffFile = New TiffFile("C:\MyMultipageTiff.tif")
            		
            		' Create a document object from the file
            		Dim MyDocument As Document = myTiffFile.GetDocument()
            		
            		' Save the PDF document
            		MyDocument.Draw("C:\MyDocument.pdf")
            		
            	End Sub
            End Module
            [C#]
            using System;
            using ceTe.DynamicPDF;
            using ceTe.DynamicPDF.Imaging;
            
            class MyClass
            {
            	static void Main()
            	{
            		// Create a TiffFile object from the TIFF image
            		TiffFile tiffFile = new TiffFile( @"C:\MyMultipageTiff.tif" );
            		
            		// Create a document object from the file
            		Document document = tiffFile.GetDocument();
            		
            		// Save the PDF document
            		document.Draw( @"C:\MyDocument.pdf" );
            	}
            }

The following example will place a Codabar bar code on the page.

            
            [Visual&nbsp;Basic]
            Imports System
            Imports ceTe.DynamicPDF
            Imports ceTe.DynamicPDF.PageElements.BarCoding
            
            Module MyModule
            		
            	Sub Main()
            		
            		' Create a PDF Document
            		Dim MyDocument As Document = New Document 
            		
            		' Create a Page and add it to the document
            		Dim MyPage As Page = New Page
            		MyDocument.Pages.Add(MyPage)
            
            		' Create a bar code
            		Dim barCode As Codabar = New Codabar("A1234B", 50, 50, 48)
            
            		' Add the bar code to the page
            		MyPage.Elements.Add(barCode)
            
            		' Save the PDF
            		MyDocument.Draw("C:\MyDocument.pdf")
            		
            	End Sub
            End Module
            [C#]
            using System;
            using ceTe.DynamicPDF;
            using ceTe.DynamicPDF.PageElements.BarCoding;
            
            class MyClass
            {
            	static void Main()
            	{
            		// Create a PDF Document
            		Document document = new Document();

            		// Create a Page and add it to the document
            		Page page = new Page();
            		document.Pages.Add( page );
            
            		// Create a barcode
            		Codabar barCode = new Codabar("A1234B", 50, 0, 48);
            
            		// Add the barcode to the page
            		page.Elements.Add( barCode );	
            
            		// Save the PDF
            		document.Draw( @"C:\MyDocument.pdf" );
            	}
            }

The following example will place a Code 128 bar code on the page.

            
            [Visual&nbsp;Basic]
            Imports System
            Imports ceTe.DynamicPDF
            Imports ceTe.DynamicPDF.PageElements.BarCoding
            
            Module MyModule
            
            	Sub Main()
            
            		' Create a PDF Document
            		Dim MyDocument As Document = New Document 
            
            		' Create a Page and add it to the document
            		Dim MyPage As Page = New Page
            		MyDocument.Pages.Add(MyPage)
            
            		' Create a bar code
            		Dim barCode As Code128 = New Code128("Code 128 Bar Code.", _
            			50, 50, 48, 0.75F)
            
            		' Add the bar code to the page
            		MyPage.Elements.Add(barCode)
            
            		' Save the PDF
            		MyDocument.Draw("C:\MyDocument.pdf")
            
            	End Sub
            End Module
            [C#]
            using System;
            using ceTe.DynamicPDF;
            using ceTe.DynamicPDF.PageElements.BarCoding;
            
            class MyClass
            {
            	static void Main()
            	{
            		// Create a PDF Document
            		Document document = new Document();

            		// Create a Page and add it to the document
            		Page page = new Page();
            		document.Pages.Add( page );
            
            		// Create a barcode
            		Code128 barCode = new Code128("Code 128 Bar Code.", 
            			50, 50, 48, 0.75F);		
            
            		// Add the barcode to the page
            		page.Elements.Add( barCode );	
            
            		// Save the PDF
            		document.Draw( @"C:\MyDocument.pdf" );
            	}
            }

