            		' Create a bar code
            		Dim barCode As Code25 = New Code25("1234567890", 50, 50, 48)
            
            		' Add the bar code to the page
            		MyPage.Elements.Add(barCode)
            
            		' Save the PDF
            		MyDocument.Draw("C:\MyDocument.pdf")
            		
            	End Sub
            End Module
