            		group.Add( new Line( 0, 100, 100, 0, 3 ) );
            		group.Add( new Line( 100, 0, 200, 100, 3 ) );
            		group.Add( new Line( 200, 100, 100, 200, 3 ) );
            		group.Add( new Line( 100, 200, 0, 100, 3 ) );
            	
