            			Color.LightGrey, 1 );
            		string htmlText = "<font face='Times'>font face, </font>" + 
            			"<font color='FF0000'>color, </font><b>bold.</b>";
            		row7.Cells.Add( new HtmlTextArea( htmlText, 0, 0, 140, 30, 
            		FontFamily.Helvetica, 16, false ) );
            
