            		lineGroup.Add( new Line( 25, 25, 125, 125, 5 ) );
            		lineGroup.Add( new Line( 25, 125, 125, 25, 5 ) );
            		Cell cell = row8.Cells.Add( lineGroup, 2 );
            		cell.Align = TextAlign.Center;
            
