            		Cell cell3 = row4.Cells.Add( "Rowheader 3", Font.HelveticaBold, 16, 
            		    Color.Black, Color.Gray, 1 );
            		cell3.Align = TextAlign.Center;
            		cell3.VAlign = VAlign.Center;
