member.example:
This example shows how to use the document in the creation of a simple PDF document.

            
            [Visual&nbsp;Basic]
            Imports System
            Imports ceTe.DynamicPDF
            Imports ceTe.DynamicPDF.PageElements
            
            Module MyModule
            
            	Sub Main()
            
            		' Create a PDF Document
            		Dim MyDocument As Document = New Document 
            
            		' Create a Page and add it to the document
            		Dim MyPage As Page = New Page
            		MyDocument.Pages.Add(MyPage)
            
            		' Add a label to the page
            		MyPage.Elements.Add(New Label("My PDF Document", 0, 0, 512, 40, Font.Helvetica, 30, TextAlign.Center))
            
            		' Save the PDF document
            		MyDocument.Draw("C:\MyDocument.pdf")
            
            	End Sub
            End Module
            [C#]
            using System;
            using ceTe.DynamicPDF;
            using ceTe.DynamicPDF.PageElements;
            
            class MyClass
            {
            	static void Main()
            	{
            		// Create a PDF Document
            		Document document = new Document();
            
            		// Create a Page and add it to the document
            		Page page = new Page();
            		document.Pages.Add( page );
            
            		// Add a label to the page 
            		Page.Elements.Add( new Label( "My PDF Document", 0, 0, 512, 40, Font.Helvetica, 30, TextAlign.Center ) );
            
            		// Save the PDF document
            		document.Draw( @"C:\MyDocument.pdf" );
            	}
            }

This example shows how to output the PDF to a byte array.

            
            [Visual&nbsp;Basic]
            Imports System
            Imports ceTe.DynamicPDF
            Imports ceTe.DynamicPDF.PageElements
            
            Module MyModule
            
            	Sub Main()
            
            		' Create a PDF Document
            		Dim MyDocument As Document = New Document 
            		
            		' Create a Page and add it to the document
            		Dim MyPage As Page = New Page
            		MyDocument.Pages.Add(MyPage)
            		
            		' Add a label to the page
            		MyPage.Elements.Add(New Label("My PDF Document", 0, 0, 512, 40, Font.Helvetica, 30, TextAlign.Center))
            
            		' Save the PDF document to a byte array
            		Private MyPdfData As Byte() = MyDocument.Draw()
            		
            	End Sub
            End Module
            
            [C#]
            using System;
            using ceTe.DynamicPDF;
            using ceTe.DynamicPDF.PageElements;
            
            class MyClass
            {
            	static void Main()
            	{
            		// Create a PDF Document
            		Document document = new Document();

            		// Create a Page and add it to the document
            		Page page = new Page();
            		document.Pages.Add( page );
            
            		// Add a label to the page 
            		Page.Elements.Add( new Label( "My PDF Document", 0, 0, 512, 40, Font.Helvetica, 30, TextAlign.Center ) );

            		// Save the PDF document to a byte array
            		byte[] pdfData = document.Draw();
            	}
            }

