The following example shows how to set several different form field's values.

            
            [Visual&nbsp;Basic]
            Imports System
            Imports ceTe.DynamicPDF
            Imports ceTe.DynamicPDF.Forms
            Imports ceTe.DynamicPDF.Merger
            
            Module MyModule
            
            	Sub Main()
            
            		' Create a MergeOptions object to import the AcroForm
            		Dim options As MergeOptions = New MergeOptions(True)
            
            		' Create a merge document with your PDF containing form fields
            		Dim MyDocument As MergeDocument = New MergeDocument( _
            			"C:\ImportPDF.pdf", options)
            
            		' Set your check box's value equal to the export value, this  
            		' will cause the check box to appear checked
            		Dim checkBox As CheckBoxField = MyDocument.Form.Fields("CB1")
            		checkBox.Value = "Yes"
            
            		' Set your combo box's value equal to the item you want selected
            		Dim comboBox As ComboBoxField = MyDocument.Form.Fields("Combo Box1")
            		comboBox.Value = "Item4"
            
            		' You may highlight multiple items in the list box
            		Dim listBox As ListBoxField = MyDocument.Form.Fields("List Box2")
            		listBox.SetValues(New String() {"Item1", "Item3", "Item5"})
            
            		' Set the radio button's value equal to the value of the radio 
            		' button you want selected
            		Dim radioButton As RadioButtonField = MyDocument.Form.Fields("RB1")
            		radioButton.Value = "Option2"
            
            		' Set the value of the text field equal to the string you want to appear
            		Dim text As TextField = MyDocument.Form.Fields("Text3")
            		text.Value = "This is my text field"
            
            		' Save the PDF
            		MyDocument.Draw("C:\MyDocument.pdf")
            			
            	End Sub
            End Module
            [C#]
            using System;
            using ceTe.DynamicPDF;
            using ceTe.DynamicPDF.Forms;
            using ceTe.DynamicPDF.Merger;
            
            class MyClass
            {
            	static void Main()
            	{
            		// Create a MergeOptions object to import the AcroForm
            		MergeOptions options = new MergeOptions( true );

            		// Create a merge document with your PDF containing form fields
            		MergeDocument document = new MergeDocument( 
            			@"C:\ImportPDF.pdf", options );
            
            		// Set your check box's value equal to the export value, this 
            		// will cause the check box to appear checked
            		CheckBoxField checkBox = (CheckBoxField)document.Form.Fields["CB1"];
            		checkBox.Value = "Yes";
            
            		// Set your combo box's value equal to the item you want selected 
            		ComboBoxField comboBox = (ComboBoxField)document.Form.Fields["Combo Box1"];
            		comboBox.Value = "Item4";
            
            		// You may highlight multiple items in the list box
            		ListBoxField listBox = (ListBoxField)document.Form.Fields["List Box2"];
            		listBox.SetValues( new string[] { "Item1", "Item3", "Item5" } );
            
            		// Set the radio button's value equal to the value of the radio 
            		// button you want selected
            		RadioButtonField radioButton = (RadioButtonField)document.Form.Fields["RB1"];
            		radioButton.Value = "Option2";
            
            		// Set the value of the text field equal to the string you want to appear
            		TextField text = (TextField)document.Form.Fields["Text3"];
            		text.Value = "This is my text field";
            
            		// Save the PDF
            		document.Draw( @"C:\MyDocument.pdf" );
            	}
            }

