This example shows you how to import one page of an existing PDF and save it as a separate PDF.

            
            [Visual&nbsp;Basic]
            Imports System
            Imports ceTe.DynamicPDF
            Imports ceTe.DynamicPDF.Merger
            
            Module MyModule
            
            	Sub Main()
            
            		' Create a PDF Document
            		Dim MyDocument As Document = New Document 
            			
            		' Create an ImportedPage and add it to the document
            		Dim MyPage As ImportedPage = New ImportedPage("C:\ImportPDF.pdf", 2, 50)
            		MyDocument.Pages.Add( MyPage )
            
            		' Save the PDF
            		MyDocument.Draw("C:\MyDocument.pdf")
            		
            	End Sub
            End Module
            [C#]
            using System;
            using ceTe.DynamicPDF;
            using ceTe.DynamicPDF.Merger;
            
            class MyClass
            {
            	static void Main()
            	{
            		// Create a PDF Document
            		Document document = new Document();

            		// Create an ImportedPage and add it to the document
            		ImportedPage page = new ImportedPage( @"C:\ImportPDF.pdf", 2, 50 );
            		document.Pages.Add( page );
            
            		// Save the PDF
            		document.Draw( @"C:\MyDocument.pdf" );
            	}
            }

This example shows you how to import one page of an existing PDF using this class.

            
            [Visual&nbsp;Basic]
            Imports System
            Imports ceTe.DynamicPDF
            Imports ceTe.DynamicPDF.Merger
            
            Module MyModule
            
            	Sub Main()
            
            		' Create a PDF Document
            		Dim MyDocument As Document = New Document 
            
            		' Create a Page and add it to the document
            		Dim MyPage As Page = New Page
            		MyDocument.Pages.Add(MyPage)
            
            		' Create an ImportedPageContents object
            		Dim MyContents As ImportedPageContents = _
            			New ImportedPageContents( "C:\ImportPDF.pdf", 2 )
            
            		' Create an ImportedPage and add it to the document
            		Dim MyData As ImportedPageData = New ImportedPageData( _
            			MyContents, -200, -100, 0.24F )
            		MyPage.Elements.Add(MyData)
            
            		' Save the PDF
            		MyDocument.Draw("C:\MyDocument.pdf")
            
            	End Sub
            End Module
            [C#]
            using System;
            using ceTe.DynamicPDF;
            using ceTe.DynamicPDF.Merger;
            
            class MyClass
            {
            	static void Main()
            	{
            		// Create a PDF Document
            		Document document = new Document();
            			
            		// Create a Page and add it to the document
            		Page page = new Page();
            		document.Pages.Add( page );
            
            		// Create an ImportedPageContents object
            		ImportedPageContents contents =
            			new ImportedPageContents( @"C:\ImportPDF.pdf", 2 );
            
            		// Create an ImportedPageData and add it to the page
            		ImportedPageData data = new ImportedPageData( 
            			contents, -200, -100, 0.24f );
            		page.Elements.Add( data );
            
            		// Save the PDF
            		document.Draw( @"C:\MyDocument.pdf" );
            	}
            }

This example shows you how to import one page of an existing PDF, scale that page and then place it on another blank page.

            
            [Visual&nbsp;Basic]
            Imports System
            Imports ceTe.DynamicPDF
            Imports ceTe.DynamicPDF.Merger
            
            Module MyModule
            
            	Sub Main()
            
            		' Create a PDF Document
            		Dim MyDocument As Document = New Document 
            
            		' Create a Page and add it to the document
            		Dim MyPage As Page = New Page
            		MyDocument.Pages.Add(MyPage)
            
            		' Create an ImportedPage and add it to the document
            		Dim MyData As ImportedPageData = New ImportedPageData( _
            			"C:\ImportPDF.pdf", 2, -200, -100, 0.24F )
            		MyPage.Elements.Add(MyData)
            
            		' Save the PDF
            		MyDocument.Draw("C:\MyDocument.pdf")
            
            	End Sub
            End Module
            [C#]
            using System;
            using ceTe.DynamicPDF;
            using ceTe.DynamicPDF.Merger;
            
            class MyClass
            {
            	static void Main()
            	{
            		// Create a PDF Document
            		Document document = new Document();
            			
            		// Create a Page and add it to the document
            		Page page = new Page();
            		document.Pages.Add( page );

            		// Create an ImportedPageData and add it to the page
            		ImportedPageData data = new ImportedPageData( 
            			@"C:\ImportPDF.pdf", 2, -200, -100, 0.24f );
            		page.Elements.Add( data );
            
            		// Save the PDF
            		document.Draw( @"C:\MyDocument.pdf" );
            	}
            }

The following example will first merge two entire PDF documents together, then will Append a selected page of 
            a third PDF to the end.

            
            [Visual&nbsp;Basic]
            Imports System
            Imports ceTe.DynamicPDF
            Imports ceTe.DynamicPDF.Merger
            
            Module MyModule
            
            	Sub Main()
            
            		' Create two PDF document objects
            		Dim MyPdfA As PdfDocument = New PdfDocument("C:\ImportPDFA.pdf")
            		Dim MyPdfB As PdfDocument = New PdfDocument("C:\ImportPDFB.pdf")
            
            		' Merge the two documents
            		Dim MyDocument As MergeDocument = MergeDocument.Merge(MyPdfA, MyPdfB)
            
            		' Append an additional document
            		Dim MyPdfC As PdfDocument = New PdfDocument("C:\MyDocumentC.pdf")
            		MyDocument.Append(MyPdfC, 2, 1)
            
            		' Save the PDF
            		MyDocument.Draw("C:\MyDocument.pdf")
            
            	End Sub
            End Module
            [C#]
            using System;
            using ceTe.DynamicPDF;
            using ceTe.DynamicPDF.Merger;
            
            class MyClass
            {
            	static void Main()
            	{
            		// Create two PDF document objects
            		PdfDocument pdfA = new PdfDocument( @"C:\ImportPDFA.pdf" );
            		PdfDocument pdfB = new PdfDocument( @"C:\ImportPDFB.pdf" );
            
            		// Merge the two documents
            		MergeDocument document = MergeDocument.Merge( pdfA, pdfB );
            
            		// Append an additional document
            		PdfDocument pdfC = new PdfDocument( @"C:\MyDocumentC.pdf" );
            		document.Append( pdfC, 2, 1);
            
            		// Save the PDF
            		document.Draw( @"C:\MyDocument.pdf" );
            	}
            }

